<script>
    $(document).ready(function(){
		setTimeout(function(){
			if ($('.checkoutHeader').hasClass('international')){
				$("body").addClass("Wholesale_customer");
			}
			var get_cl_nm = $('.checkoutHeader').attr('data-name').replace(/ /g,"");
			$('body').addClass(get_cl_nm+'_customer');
			$('body').find('[value="cod"]').parent().hide(); 
			$('body').find('[value="cod"]').parent().addClass(get_cl_nm+'_customer');    
			
			setTimeout(function(){
				if ($('body').hasClass('internationalwholesaler_customer')){
					$('body').find('#shippingPostalCode').parent('div').hide();
					setTimeout(function(){
						$('body').find('#shippingPostalCode').attr('value', '00000');
					},5000);
				}  
			},3000);
		},2500);  
	}); 
</script>
    
